    public static boolean checkInclusion(String s1, String s2) {
        int [] map = new int[26];
        int [] copy;
        int l = 0, r = 0;

        for (char c : s1.toCharArray()) {
            map[c - 'a']++;
        }

        copy = map.clone();

        while (r < s2.length()) {
            if (r - l == s1.length()) return true;

            char c = s2.charAt(r);
            if (copy[c - 'a'] > 0) {
                copy[c - 'a']--;
                r++;
            } else {
                if (map[c - 'a'] > 0) {
                    if (s2.charAt(l) == c) {
                        l++;
                        r++;
                    } else {
                        copy[s2.charAt(l) -'a']++;
                        l++;
                    }
                } else {
                    copy = map.clone();
                    r++;
                    l = r;
                }
            }
        }

        return r - l == s1.length();
    }

    // beats 56.98% 8ms
